using UnityEngine; using System.Collections;  public class Main : MonoBehaviour {  	private int ramx; 	private int ramh; 	private int ramz; 	private float ramt = 1; 	public GameObject yellow; 	public GameObject blue; 	public GameObject pink; 	public GameObject green;  	// Use this for initialization 	void Start () {  	}  	// Update is called once per frame 	void Update () { 		ramt -= Time.deltaTime;  		if (ramt <= 0) { 			ramx = Random.Range (-4, 4); 			ramh = Random.Range (1, 4); 			ramt = Random.Range (0.2f, 5); 			ramz = Random.Range (-20, 15); 			if (ramh == 1){ 				Instantiate (yellow); 				yellow.transform.position = new Vector3 (ramx, 0, ramz); 			}  else if (ramh == 2){ 				Instantiate (blue); 				blue.transform.position = new Vector3 (ramx, 0, ramz); 			}  else if (ramh == 3){ 				Instantiate (pink); 				pink.transform.position = new Vector3 (ramx, 0, ramz); 			}  else if (ramh == 4){ 				Instantiate (green); 				green.transform.position = new Vector3 (ramx, 0, ramz); 			} 		} 	} }